import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useSelector } from "react-redux";

import Transactions from "../pages/Transactions";

const useStyles = makeStyles({
	root: {
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-evenly",
		borderBottom: "5px solid #e6e6e6",
		alignItems: "center",
		margin: "100px auto ",
		width: "60%"
	},
	title: {
		color: "#a12bcc",
		textAlign: "center",
		margin: "0 auto 50px auto"
	}
});

const TransactionsContainer = props => {
	const classes = useStyles();
	const currentUser = useSelector(state => state.login.currentUser);
	console.log(currentUser.transactions);
	const filterUniqueTransactions = transactions => {
		let uniqueTickers = new Set();
		let filteredTransactions = [];
		transactions.filter(transaction => {
			if (!uniqueTickers.has(transaction.stock.ticker)) {
				// debugger;
				uniqueTickers.add(transaction);
			} else {
				transactions.splice(transaction, 1);
			}
		});

		uniqueTickers.forEach(transaction =>
			filteredTransactions.push(transaction)
		);
		// debugger;
		return filteredTransactions;
	};
	filterUniqueTransactions(currentUser.transactions);

	// const renderTransactions = () => {
	// 	return transactions.map(transaction => {
	// 		return <Transactions key={transaction.id} transaction={transaction} />;
	// 	});
	// };
	return (
		<div className={classes.root}>
			<h1 className={classes.title}>Transactions</h1>
			{/* {renderTransactions()} */}
		</div>
	);
};

export default TransactionsContainer;
