import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@material-ui/core";

const useStyles = makeStyles({
	purchaseForm: {
		display: "flex",
		flexDirection: "column",
		width: "30%",
		margin: "0 auto"
	},
	label: {
		textAlign: "center"
	},
	input: {
		textAlign: "center",
		padding: "10px",
		margin: "10px auto 0px auto"
	},
	button: {
		width: "60%",
		borderRadius: "10px",
		background: "#a12bcc",
		color: "whitesmoke",
		margin: "20px auto 0 auto",
		"&:hover": {
			background: "#a12bcc",
			color: "whitesmoke"
		}
	},
	low: {
		color: "#f50c2b"
	},
	even: {
		color: "grey"
	},
	high: {
		color: "#0af229"
	},
	ticker: {
		textAlign: "center"
	},
	userBalance: {
		textAlign: "center"
	}
});
const PurchaseForm = props => {
	const classes = useStyles();
	const dispatch = useDispatch();
	const currentUser = useSelector(state => state.login.currentUser);
	const currentStock = useSelector(state => state.stock.currentStock);
	console.log("currentStock", currentStock);
	const renderTicker = () => {
		if (currentStock.current_price > currentStock.open_price) {
			return (
				<h3 className={(classes.high, classes.ticker)}>
					Ticker: {currentStock.ticker}
				</h3>
			);
		} else if (currentStock.current_price < currentStock.open_price) {
			return (
				<h3 className={(classes.low, classes.ticker)}>
					Ticker: {currentStock.ticker}
				</h3>
			);
		} else {
			return (
				<h3 className={(classes.med, classes.ticker)}>
					Ticker: {currentStock.ticker}
				</h3>
			);
		}
	};
	return (
		<div>
			<h2 className={classes.userBalance}>CASH - ${currentUser.balance}</h2>
			{renderTicker()}
			<form className={classes.purchaseForm}>
				<label className={classes.label}>QTY:</label>
				<input
					className={classes.input}
					type="text"
					placeholder="Quantity"
					handleChange={null}
				/>
				<Button className={classes.button} variant="outlined">
					Buy
				</Button>
			</form>
		</div>
	);
};

export default PurchaseForm;
